package exercices_1;

/*2. Write a Java program to print the sum of six numbers. Go to the editor
Test Data: 
74 + 36 +5 +10+15+10
Expected Output :
150*/


public class Exo02 {
	
	public static void printSum(int nb1, int nb2, int nb3, int nb4, int nb5, int nb6) {
		
		System.out.println(nb1 + nb2 + nb3 + nb4 + nb5 + nb6);
	
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		printSum(74, 36, 5, 10, 15, 10);
	}

}
